    private void generateButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_generateButtonActionPerformed
        // TODO add your handling code here:

        file = fileName.getText();                                      //    Get text in the text field
        excelLocation = fileLocation.getText();
        sheetName = sheetNameTextField.getText();
        
        CompSciIA run = new CompSciIA();                    //     Class where the method 'run'  is
        
        try {
            run.run();                                                          //      Run all the procedures
        } catch (IOException ex) {
            Logger.getLogger(GeneratorGUI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_generateButtonActionPerformed

